import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link, useHistory } from 'react-router-dom';
import '../styles/login.css';

const Login = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [tasks, setTasks] = useState([]); // Store tasks after login
  const history = useHistory();
  const handleCredentialResponse = (response) => {
    console.log("Encoded JWT ID token: " + response.credential);
    // Handle authentication response here (e.g., send to backend)
  };

  useEffect(() => {
    const loadGoogleScript = () => {
      const script = document.createElement("script");
      script.src = "https://accounts.google.com/gsi/client";
      script.async = true;
      script.defer = true;
      script.onload = () => {
        if (window.google) {
          window.google.accounts.id.initialize({
            client_id: "797138949978-0dsb0nanfgqgo7n8u8u1mouq4kouriop.apps.googleusercontent.com",
            callback: handleCredentialResponse,
          });
          window.google.accounts.id.renderButton(
            document.getElementById("googleSignInButton"),
            { theme: "outline", size: "large" }
          );
        }
      };
      document.body.appendChild(script);
    };
  
    loadGoogleScript();
  }, []);
  
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post('http://localhost:5123/login', { username, password });
      const { user_id } = res.data; // Get user_id from backend
      if (user_id) {
        localStorage.setItem('user_id', String(user_id)); // Store user_id
        fetchTasks(user_id); // Fetch tasks after login
        history.push('/addtask'); // Redirect to Add Tasks page
      }
    } catch (error) {
      console.error('Error logging in:', error);
      alert('Login failed');
    }
  };

  const fetchTasks = async (user_id) => {
    try {
      const res = await axios.get(`http://localhost:5123/tasks/${user_id}`);
      setTasks(res.data); // Set tasks for logged-in user
    } catch (error) {
      console.error("Error fetching tasks:", error);
    }
  };

  return (
    <div className="login-container">
      <Link to="/" className="login-back-button">‚Üê Back</Link>

      <div className="login-form">
        <h2>Login</h2>
        <form onSubmit={handleSubmit}>
          <input
            type="text"
            placeholder="Username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            required
          />
          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
          <button type="submit">Log In</button>
          <div id="googleSignInButton"></div>
        </form>
      </div>

      {/* Display tasks if available */}
      {tasks.length > 0 && (
        <div className="tasks-list">
          <h3>Your Tasks</h3>
          <ul>
            {tasks.map((task) => (
              <li key={task._id}>{task.text}</li>
            ))}
          </ul>
        </div>
      )}

      <p className="lauth-quote">"People's dreams never END!"</p>
      <p className="lauth">- Marshall D. Teach</p>
    </div>
  );
};

export default Login;
