import React, { useState } from 'react';
import axios from 'axios';
import { Link, useHistory } from 'react-router-dom';
import '../styles/addtask.css';

const AddTask = ({ setTasks }) => {
  const [taskName, setTaskName] = useState('');
  const [dueDate, setDueDate] = useState('');
  const [category, setCategory] = useState('');
  const history = useHistory();

  const handleAddTask = async (e) => {
    e.preventDefault();
  
    const user_id = localStorage.getItem('user_id');
    const userToken = localStorage.getItem('userToken');
  
    if (!user_id || !userToken) {
      alert("User not logged in. Please login first.");
      history.push('/login'); // Redirect to login page
      return;
    }
  
    try {
      const res = await axios.post(
        'http://localhost:5123/tasks',
        {
          task_name: taskName,
          due_date: dueDate,
          category,
          user_id: user_id.trim(), // Ensure no extra spaces
        },
        {
          headers: {
            Authorization: `Bearer ${userToken}`, // Include the token for authentication
          },
        }
      );
  
      if (res.status === 201) {
        console.log("Task added:", res.data);
        setTasks(prevTasks => [...prevTasks, res.data]); // Update tasks state
        alert("Task added successfully!");
      }
  
      // Clear form fields
      setTaskName('');
      setDueDate('');
      setCategory('');
    } catch (error) {
      console.error('Error adding task:', error.response?.data || error.message);
      alert(error.response?.data?.error || "Failed to add task. Please try again.");
    }
  };

  const handleLogout = () => {
    // Clear local storage
    localStorage.removeItem('userToken');
    localStorage.removeItem('user_id');
    localStorage.removeItem('userData');
    
    // Redirect to login page
    history.push('/login');
  };

  return (
    <div className="add-task-container">
      <h2>Add New Task</h2>

      <form className="add-task-form" onSubmit={handleAddTask}>
        <input
          type="text"
          className="add-task-input"
          placeholder="Task Name"
          value={taskName}
          onChange={(e) => setTaskName(e.target.value)}
          required
        />
        <input
          type="date"
          className="add-task-input"
          value={dueDate}
          onChange={(e) => setDueDate(e.target.value)}
          required
        />
        <input
          type="text"
          className="add-task-input"
          placeholder="Category"
          value={category}
          onChange={(e) => setCategory(e.target.value)}
        />
        <button className="add-task-button" type="submit">Add Task</button>
      </form>

      <button onClick={handleLogout} className="logout-link">Logout</button>
    </div>
  );
};

export default AddTask;
