import React, { useState } from 'react';
import axios from 'axios';
import { GoogleLogin } from 'react-google-login';
import { Link, useNavigate } from 'react-router-dom';
import '../styles/login.css';
import Button from '@mui/material/Button'; // ✅ Import Button from MUI
import Icon from '../components/icon'; // ✅ Ensure proper capitalization

const Login = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate(); // ✅ Corrected useHistory to useNavigate

  const googleSuccess = async (res) => {
    console.log("Google login success:", res);
    // TODO: Handle Google login response (send token to backend)
  };

  const googleFailure = () => {
    console.log("Google Sign In was unsuccessful. Try again later");
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post('http://localhost:5123/login', { username, password });
      const { user_id } = res.data;
      if (user_id) {
        localStorage.setItem('user_id', String(user_id));
        navigate('/addtask'); // ✅ Corrected navigation
      }
    } catch (error) {
      console.error('Error logging in:', error);
      alert('Login failed');
    }
  };

  return (
    <div className="login-container">
      <Link to="/" className="login-back-button">← Back</Link>
      <div className="login-form">
        <h2>Login</h2>
        <form onSubmit={handleSubmit}>
          <input
            type="text"
            placeholder="Username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            required
          />
          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
          <button type="submit">Log In</button>

          {/* ✅ Fixed Google Login */}
          <GoogleLogin
            clientId="797138949978-0dsb0nanfgqgo7n8u8u1mouq4kouriop.apps.googleusercontent.com"
            render={(renderProps) => (
              <Button
                color="primary"
                fullWidth
                onClick={renderProps.onClick}
                disabled={renderProps.disabled}
                startIcon={<Icon />}
                variant="contained"
              >
                Google Sign In
              </Button>
            )}
            onSuccess={googleSuccess}
            onFailure={googleFailure}
            cookiePolicy="single_host_origin"
          />
        </form>
      </div>
      <p className="lauth-quote">"People's dreams never END!"</p>
      <p className="lauth">- Marshall D. Teach</p>
    </div>
  );
};

export default Login;
