import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Switch, Route, Redirect } from "react-router-dom";
import axios from "axios";
import { GoogleOAuthProvider } from "@react-oauth/google"; // ✅ Google OAuth Provider

import AddTask from "./components/AddTask";
import TaskList from "./components/TaskList";
import Signup from "./pages/Signup";
import Login from "./pages/Login";
import Home from "./pages/Home";
import SearchResult from "./pages/SearchResult";
import SearchBar from "./components/SearchBar"; // ✅ Import SearchBar

const App = () => {
  const [tasks, setTasks] = useState([]);
  const [searchTerm, setSearchTerm] = useState(""); // ✅ Store search term globally

  // ✅ Function to check if user is logged in
  const isAuthenticated = () => {
    return !!localStorage.getItem("user_id");
  };

  useEffect(() => {
    const fetchTasks = async () => {
      const user_id = localStorage.getItem("user_id");
      if (!user_id) return;

      try {
        const res = await axios.get(`http://localhost:5123/tasks/${user_id}`);
        setTasks(res.data);
      } catch (error) {
        console.error("Error fetching tasks:", error);
      }
    };

    fetchTasks();
  }, []);

  return (
    <GoogleOAuthProvider clientId={process.env.REACT_APP_GOOGLE_CLIENT_ID}>
      <Router>
        <div className="App">
          <Switch>
            <Route path="/signup" component={Signup} />
            <Route path="/login" component={Login} />
            <Route path="/" exact component={Home} />
            <Route path="/searchresults">
              <SearchResult searchTerm={searchTerm} />
            </Route>

            {/* ✅ Protected Route for /addtask */}
            <Route path="/addtask">
              {isAuthenticated() ? (
                <>
                  <SearchBar setSearchTerm={setSearchTerm} />
                  <AddTask setTasks={setTasks} />
                  <TaskList tasks={tasks} setTasks={setTasks} />
                </>
              ) : (
                <Redirect to="/login" /> // ❌ Redirect to login if not authenticated
              )}
            </Route>
          </Switch>
        </div>
      </Router>
    </GoogleOAuthProvider>
  );
};

export default App;
