import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link, useNavigate } from "react-router-dom"; // ✅ Use useNavigate instead of useHistory

import "../styles/signup.css";

const SignUp = () => {
  const navigate = useNavigate(); // ✅ Correct navigation hook

  const [username, setUsername] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [isRegistered, setIsRegistered] = useState(false);

  useEffect(() => {
    if (window.google) {
      window.google.accounts.id.initialize({
        client_id: "797138949978-0dsb0nanfgqgo7n8u8u1mouq4kouriop.apps.googleusercontent.com",
        callback: handleCredentialResponse,
      });

      window.google.accounts.id.renderButton(
        document.getElementById("googleSignUpButton"),
        { theme: "outline", size: "large" }
      );
    } else {
      console.error("Google SDK not loaded.");
    }
  }, []);

  const handleCredentialResponse = async (response) => {
    console.log("Google JWT Token:", response.credential);
    await handleGoogleSignIn(response.credential); // ✅ Send only the JWT token
  };

  const handleGoogleSignIn = async (token) => {
    try {
      const res = await axios.post("http://localhost:5123/api/auth/google-login", { token }); // ✅ Correct backend URL
      console.log("Google Login successful:", res.data);
      alert("Google Login successful!");
      navigate("/addtask"); // ✅ Redirect on success
    } catch (error) {
      console.error("Google Sign-In Error:", error.response?.data || error.message);
      alert("Google Sign-In failed");
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post("http://localhost:5123/signup", { username, email, password });
      if (res.status === 201) {
        alert("Signup successful!");
        navigate("/addtask"); // ✅ Correct redirection
      }
    } catch (error) {
      console.error("Error signing up:", error.response?.data || error.message);
      alert("Signup failed");
    }
  };

  return (
    <div className="signup-container">
      <Link to="/" className="signup-back-button">← Back</Link>

      <div className="signup-form">
        <h2>Sign Up</h2>
        <form onSubmit={handleSubmit}>
          <input
            type="text"
            placeholder="Username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            required
          />
          <input
            type="email"
            placeholder="Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
          <button type="submit">Sign Up</button>
          <div id="googleSignUpButton"></div>
        </form>
        {isRegistered && <p>Registration successful! <Link to="/login">Login here</Link></p>}
      </div>
      <p className="auth-quote">"A journey of a thousand miles begins with a single step."</p>
    </div>
  );
};

export default SignUp;
