import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { motion, AnimatePresence } from 'framer-motion';
import '../styles/tasklist.css';

const TaskList = ({ tasks, setTasks }) => {
  const [error, setError] = useState(null); // ✅ Define setError

  // Fetch tasks on component mount
  useEffect(() => {
    fetchTasks();
  }, []);

  // Fetch all tasks
  const fetchTasks = async () => {
    try {
      const res = await axios.get('http://localhost:5123/tasks', {
        headers: {
          Authorization: `Bearer ${localStorage.getItem('userToken')}`,
        },
      });
      setTasks(res.data);
    } catch (error) {
      console.error('Error fetching tasks:', error);
      setError('Failed to fetch tasks. Please try again.');
    }
  };

  // Handle task search
  const handleSearch = async (searchTerm) => {
    try {
      const res = await axios.post(
        'http://localhost:5123/tasks/search',
        { searchTerm },
        {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('userToken')}`,
          },
        }
      );
      setTasks(res.data);
    } catch (error) {
      console.error('Search failed:', error);
      setError('Search failed. Please try again.');
    }
  };

  // Handle task deletion
  const handleDeleteTask = async (taskId) => {
    try {
      const res = await axios.delete(`http://localhost:5123/tasks/${taskId}`, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem('userToken')}`,
        },
      });

      if (res.status === 200) {
        alert('Task deleted successfully!');
        fetchTasks(); // Refresh the task list
      } else {
        alert('Failed to delete task. Please try again.');
      }
    } catch (error) {
      console.error('Delete task error:', error);
      setError(error.response?.data?.message || 'Failed to delete task. Please try again.');
    }
  };

  // Toggle task completion status
  const toggleTaskCompletion = async (taskId, currentStatus) => {
    try {
      const response = await axios.put(
        `http://localhost:5123/tasks/${taskId}`,
        { completed: !currentStatus },
        {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('userToken')}`,
          },
        }
      );
  
      if (response.status === 200) {
        // Update the task in the state
        setTasks((prevTasks) =>
          prevTasks.map((task) =>
            task._id === taskId ? { ...task, completed: !currentStatus } : task
          )
        );
      }
    } catch (error) {
      console.error('Error updating the task:', error);
      setError('Failed to update task. Please try again.');
    }
  };

  // Filter tasks
  const incompleteTasks = tasks.filter((task) => !task.completed);
  const completedTasks = tasks.filter((task) => task.completed);

  return (
    <div className="task-list-container">
      {/* Pending Tasks Section */}
      {incompleteTasks.length > 0 && (
        <>
          <h2 className="task-list-heading1">Pending Tasks</h2>
          <table className="task-table">
            <thead>
              <tr>
                <th>Task Name</th>
                <th>Due Date</th>
                <th>Category</th>
                <th>Completed</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              <AnimatePresence>
                {incompleteTasks.map((task) => (
                  <motion.tr
                    key={task._id}
                    initial={{ opacity: 0, y: -10 }}
                    animate={{ opacity: 1, y: 0 }}
                    exit={{ opacity: 0, y: -10 }}
                    transition={{ duration: 0.3 }}
                    className="pending-task"
                  >
                    <td>{task.task_name}</td>
                    <td>{new Date(task.due_date).toLocaleDateString()}</td>
                    <td>{task.category}</td>
                    <td>
                      <input
                        type="checkbox"
                        checked={task.completed}
                        onChange={() => toggleTaskCompletion(task._id, task.completed)}
                      />
                    </td>
                    <td>
                      <motion.button
                        className="delete"
                        whileHover={{ scale: 1.1 }}
                        whileTap={{ scale: 0.9 }}
                        onClick={() => handleDeleteTask(task._id)} // ✅ Use handleDeleteTask
                      >
                        Delete
                      </motion.button>
                    </td>
                  </motion.tr>
                ))}
              </AnimatePresence>
            </tbody>
          </table>
        </>
      )}

      {/* Completed Tasks Section */}
      {completedTasks.length > 0 && (
        <>
          <h2 className="task-list-heading2">Completed Tasks</h2>
          <table className="task-table">
            <thead>
              <tr>
                <th>Task Name</th>
                <th>Due Date</th>
                <th>Category</th>
                <th>Completed</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              <AnimatePresence>
                {completedTasks.map((task) => (
                  <motion.tr
                    key={task._id}
                    initial={{ opacity: 0, y: -10 }}
                    animate={{ opacity: 1, y: 0 }}
                    exit={{ opacity: 0, y: -10 }}
                    transition={{ duration: 0.3 }}
                    className="completed-task"
                  >
                    <td>{task.task_name}</td>
                    <td>{new Date(task.due_date).toLocaleDateString()}</td>
                    <td>{task.category}</td>
                    <td>
                      <input
                        type="checkbox"
                        checked={task.completed}
                        onChange={() => toggleTaskCompletion(task._id, task.completed)}
                      />
                    </td>
                    <td>
                      <motion.button
                        className="delete"
                        whileHover={{ scale: 1.1 }}
                        whileTap={{ scale: 0.9 }}
                        onClick={() => handleDeleteTask(task._id)} // ✅ Use handleDeleteTask
                      >
                        Delete
                      </motion.button>
                    </td>
                  </motion.tr>
                ))}
              </AnimatePresence>
            </tbody>
          </table>
        </>
      )}

      {/* Error Display */}
      {error && <div className="error-message">{error}</div>}
    </div>
  );
};

export default TaskList;
