import React, { useState } from "react";
import axios from "axios";
import { GoogleLogin } from "@react-oauth/google";
import { jwtDecode } from "jwt-decode"; // ✅ To decode Google JWT token
import { Link, useHistory } from "react-router-dom";
import "../styles/login.css";

const Login = () => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const history = useHistory(); // ✅ Correct navigation method

  // ✅ Google Authentication Success Handler
  const googleSuccess = async (response) => {
    try {
      const res = await axios.post('/auth/google-login', {
        token: response.credential,
      });
  
      if (res.status === 201) {
        // New user: Signup successful
        alert('Signup successful! Welcome to the app.');
      } else if (res.status === 200) {
        // Existing user: Login successful
        alert('Login successful!');
      }
  
      // Store user data and token
      localStorage.setItem('userToken', res.data.token);
      localStorage.setItem('user_id', res.data.user.id);
      localStorage.setItem('userData', JSON.stringify(res.data.user));
  
      // Redirect to the desired page
      history.push('/addtask');
    } catch (error) {
      console.error('Google Sign-In Error:', error);
      alert('An error occurred during Google Sign-In. Please try again.');
    }
  };
  // ❌ Google Authentication Failure Handler
  const googleFailure = () => {
    console.log("Google Sign-In was unsuccessful. Try again later");
  };

  // ✅ Manual Login Submission
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const loginRes = await axios.post("http://localhost:5123/auth/login", {
        username,
        password,
      });
  
      const { user_id } = loginRes.data;
      if (user_id) {
        localStorage.setItem("user_id", String(user_id));
        history.push("/addtask");
      }
    } catch (error) {
      console.error("Error logging in:", error);
      alert("Login failed");
    }
  };
  // Removed duplicate googleSuccess function
  return (
    <div className="login-container">
      <Link to="/" className="login-back-button">← Back</Link>
      <div className="login-form">
        <h2>Login</h2>
        <form onSubmit={handleSubmit}>
          <input
            type="text"
            placeholder="Username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            required
          />
          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
          <button type="submit">Log In</button>

          {/* ✅ Google Login Button */}
          <GoogleLogin
            onSuccess={googleSuccess}
            onError={googleFailure}
          />
        </form>
      </div>
      <p className="lauth-quote">"People's dreams never END!"</p>
      <p className="lauth">- Marshall D. Teach</p>
    </div>
  );
};

export default Login;
