import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link, useHistory } from "react-router-dom";
import "../styles/signup.css";

const SignUp = () => {
  const [username, setUsername] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const history = useHistory(); // For navigation

  useEffect(() => {
    if (window.google) {
      window.google.accounts.id.initialize({
        client_id: "YOUR_GOOGLE_CLIENT_ID",
        callback: handleGoogleSignup,
      });

      window.google.accounts.id.renderButton(
        document.getElementById("googleSignUpButton"),
        { theme: "outline", size: "large" }
      );
    }
  }, []);

  // ✅ Handle Google Sign-Up
  const handleGoogleSignup = async (response) => {
    console.log("Google JWT Token:", response.credential);

    try {
      const res = await axios.post("http://localhost:5123/google-signup", {
        token: response.credential,
      });

      if (res.status === 201) {
        alert("Signup successful! Redirecting to Add Task...");
        history.push("/addtask");
      } else if (res.status === 409) {
        alert("Account already exists! Please sign in.");
      }
    } catch (error) {
      console.error("Google Signup Error:", error);
      alert("Signup failed. Please try again.");
    }
  };

  return (
    <div className="signup-container">
      <Link to="/" className="signup-back-button">← Back</Link>

      <div className="signup-form">
        <h2>Sign Up</h2>
        <form>
          <input type="text" placeholder="Username" value={username} onChange={(e) => setUsername(e.target.value)} required />
          <input type="email" placeholder="Email" value={email} onChange={(e) => setEmail(e.target.value)} required />
          <input type="password" placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)} required />
          <button type="submit">Sign Up</button>
        </form>

        <h3>Or Sign Up with Google</h3>
        <div id="googleSignUpButton"></div>
      </div>
    </div>
  );
};

export default SignUp;
