import React, { useState, useEffect } from 'react';
import axios from 'axios';

const TaskList = () => {
  const [tasks, setTasks] = useState([]);

  useEffect(() => {
    const fetchTasks = async () => {
      try {
        const res = await axios.get('http://localhost:5167/tasks');
        setTasks(res.data);
      } catch (error) {
        console.error(error);
        alert('Error fetching tasks');
      }
    };

    fetchTasks();
  }, []);

  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:5167/tasks/${id}`);
      setTasks(tasks.filter(task => task.id !== id)); // Remove task from UI
    } catch (error) {
      console.error('Error deleting task:', error);
      alert('Error deleting task');
    }
  };
  const toggleTaskCompletion = async (taskId, currentStatus) => {
    try {
      const response = await fetch(`http://localhost:5167/tasks/${taskId}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ completed: !currentStatus }),
      });
  
      if (!response.ok) {
        throw new Error('Failed to update task');
      }
  
      console.log('Task updated successfully');
    } catch (error) {
      console.error('Error updating the task:', error);
    }
  };
  
  

  return (
    <div className="task-list">
      <h2>Task List</h2>
      <table>
        <thead>
          <tr>
            <th>Task Name</th>
            <th>Due Date</th>
            <th>Category</th>
            <th>Completed</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {tasks.map((task) => (
            <tr key={task.id}>
              <td>{task.task_name}</td>
              <td>{new Date(task.due_date).toLocaleDateString()}</td>
              <td>{task.category}</td>
              <td>{task.completed ? '✅' : '❌'}</td>
              <td>
              <button onClick={() => toggleTaskCompletion(task.id, task.completed)}>Completed</button>
              </td>
              <td>
                <button
                  className="delete"
                  onClick={() => handleDelete(task.id)}
                >
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default TaskList;
