import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import '../styles/search.css';

const SearchResult = () => {
  const [tasks, setTasks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const user_id = localStorage.getItem('user_id');
  const searchTerm = localStorage.getItem('searchTerm');

  useEffect(() => {
    let isMounted = true; // Track if the component is mounted

    const fetchResults = async () => {
      try {
        const res = await axios.post(
          'http://localhost:5123/tasks/search', // ✅ Add base URL
          { searchTerm },
          {
            headers: {
              Authorization: `Bearer ${localStorage.getItem('userToken')}`,
            },
          }
        );

        if (isMounted) {
          setTasks(res.data);
          setLoading(false);
        }
      } catch (err) {
        if (isMounted) {
          setError('Failed to fetch results. Please try again.');
          setLoading(false);
        }
      }
    };

    if (searchTerm && user_id) {
      fetchResults();
    } else {
      setLoading(false);
    }

    // Cleanup function
    return () => {
      isMounted = false;
    };
  }, [searchTerm, user_id]); // ✅ Add user_id to dependencies

  if (loading) {
    return <div className="search-container">Loading...</div>;
  }

  if (error) {
    return <div className="search-container">{error}</div>;
  }

  return (
    <div className="search-container">
      <h2 className="search-heading">Search Results for "{searchTerm}"</h2>

      {tasks.length > 0 ? (
        <table className="search-table">
          <thead>
            <tr>
              <th>Task Name</th>
              <th>Category</th>
              <th>Due Date</th>
              <th>Status</th>
            </tr>
          </thead>
          <tbody>
            {tasks.map((task) => (
              <tr
                key={task._id}
                className={task.completed ? 'completed-task' : 'pending-task'}
              >
                <td>{task.task_name}</td>
                <td>{task.category}</td>
                <td>
                  {task.due_date
                    ? new Date(task.due_date).toLocaleDateString()
                    : 'No Due Date'}
                </td>
                <td>{task.completed ? 'Completed' : 'Pending'}</td>
              </tr>
            ))}
          </tbody>
        </table>
      ) : (
        <p>No tasks found.</p>
      )}

      <Link to="/addtask" className="back-link">
        Back to Add Task
      </Link>
    </div>
  );
};

export default SearchResult;
