import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link, useHistory } from "react-router-dom";

import "../styles/signup.css";

const SignUp = () => {
  const history = useHistory(); // ✅ Correct way to handle navigation

  const [username, setUsername] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  useEffect(() => {
    if (window.google) {
      window.google.accounts.id.initialize({
        client_id:
          "797138949978-0dsb0nanfgqgo7n8u8u1mouq4kouriop.apps.googleusercontent.com",
        callback: handleCredentialResponse,
      });

      window.google.accounts.id.renderButton(
        document.getElementById("googleSignUpButton"),
        { theme: "outline", size: "large" }
      );
    }
  }, []);

  const handleCredentialResponse = async (response) => {
    console.log("Google JWT Token:", response.credential);
    handleGoogleSignIn(response.credential);
  };

  const handleGoogleSignIn = async (token) => {
    try {
      const res = await axios.post(
        "http://localhost:5123/auth/google-login",
        { token }
      );
    // Store user data and token
      localStorage.setItem("userToken", res.data.token);
      localStorage.setItem("user_id", res.data.user.id); // ✅ Store user_id
      localStorage.setItem("userData", JSON.stringify(res.data.user));
      alert("Google Login Successful!");
      history.push("/addtask"); // ✅ Redirect to Add Task page
    } catch (error) {
      console.error("Google Sign-In Error:", error);
      alert("Google login failed. Try again.");
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post("http://localhost:5123/auth/signup", {
        username,
        email,
        password,
      });

      if (res.status === 201) {
        alert("Signup successful!");
        history.push("/addtask"); // ✅ Redirect after successful signup
      }
    } catch (error) {
      console.error("Error signing up:", error);
      alert("Signup failed");
    }
  };

  return (
    <div className="signup-container">
      <Link to="/" className="signup-back-button">
        ← Back
      </Link>

      <div className="signup-form">
        <h2>Sign Up</h2>
        <form onSubmit={handleSubmit}>
          <input
            type="text"
            placeholder="Username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            required
          />
          <input
            type="email"
            placeholder="Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
          <button type="submit">Sign Up</button>
        </form>

        <div id="googleSignUpButton"></div>
      </div>
    </div>
  );
};

export default SignUp;
