import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link, useHistory } from 'react-router-dom';

import '../styles/signup.css';

const SignUp = () => {
  const [username, setUsername] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [isRegistered, setIsRegistered] = useState(false);

  useEffect(() => {
    window.google.accounts.id.initialize({
      client_id: "797138949978-0dsb0nanfgqgo7n8u8u1mouq4kouriop.apps.googleusercontent.com",
      callback: handleCredentialResponse,
    });
    window.google.accounts.id.renderButton(
      document.getElementById("googleSignUpButton"),
      { theme: "outline", size: "large" }
    );
  }, []);

  const handleCredentialResponse = async (response) => {
    console.log("Google JWT Token:", response.credential);
    
    try {
      const res = await axios.post("http://localhost:5123/auth/google", {
        token: response.credential,
      });
  
      console.log("Server Response:", res.data);
      if (res.status === 200) {
        history.push("/addtask"); // Redirect after successful authentication
      }
    } catch (error) {
      console.error("Google Sign-In Error:", error);
      alert("Google sign-in failed");
    }
  };
  
  axios.post("http://localhost:5123/auth/google", { token })
    .then(response => console.log("✅ Server Response:", response.data))
    .catch(error => console.error("❌ Axios Error:", error.response?.data || error.message));

  const history = useHistory(); // Add this inside the component

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post('http://localhost:5123/signup', { username, email, password });
      if (res.status === 201) {
        alert('Signup successful!');
        history.push('/addtask'); // Redirect after successful signup
      }
    } catch (error) {
      console.error('Error signing up:', error);
      alert('Signup failed');
    }
  };
  
  const handleGoogleSignIn = async (credentialResponse) => {
    try {
      const { credential } = credentialResponse; // Token received from Google
  
      const response = await axios.post(
        "http://localhost:5123/auth/google",
        { token: credential },
        {
          validateStatus: (status) => status >= 200 && status < 500, // Prevent Axios from throwing errors
        }
      );
  
      if (response.status === 401) {
        console.log("Unauthorized: Invalid Token");
      } else if (response.status === 201) {
        console.log("Signup successful", response.data);
      } else {
        console.log("Other response:", response.data);
      }
    } catch (error) {
      console.error("Google Sign-In Error:", error);
    }
  };
  
  return (
    <div className="signup-container">
      <Link to="/" className="signup-back-button">← Back</Link>

      <div className="signup-form">
        <h2>Sign Up</h2>
        <form onSubmit={handleSubmit}>
          <input
            type="text"
            placeholder="Username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            required
          />
          <input
            type="email"
            placeholder="Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
          <button type="submit">Sign Up</button>
          <div id="googleSignUpButton"></div>
        </form>
        {isRegistered && <p>Registration successful! <Link to="/login">Login here</Link></p>}
      </div>
      <p className="auth-quote">"A journey of a thousand miles begins with a single step."</p>
    </div>
  );
};

export default SignUp;
